-- Table structure for cst_wealth_info
-- ----------------------------
DROP TABLE IF EXISTS `cst_wealth_info`;
CREATE TABLE `cst_wealth_info`  (
  `wid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '方案ID',
  `uid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '主题ID或会员ID或分类ID或项目ID',
  `lasttime` int(11) NOT NULL DEFAULT 0 COMMENT '最后一次更新时间',
  `val` float UNSIGNED NOT NULL DEFAULT 0 COMMENT '最小财富为0，不考虑负数情况',
  PRIMARY KEY (`wid`, `uid`) USING BTREE
) ENGINE = MyISAM CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财富内容' ROW_FORMAT = Fixed;

-- ----------------------------
-- Records of cst_wealth_info
-- ----------------------------

-- ----------------------------
-- Table structure for cst_wealth_log
-- ----------------------------
DROP TABLE IF EXISTS `cst_wealth_log`;
CREATE TABLE `cst_wealth_log`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `wid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '财富ID',
  `rule_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '规则ID',
  `goal_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '目标会员ID',
  `mid` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主键ID关联',
  `val` float NOT NULL DEFAULT 0 COMMENT '不带负号表示增加，带负号表示减去',
  `note` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '操作摘要',
  `appid` enum('admin','www','api') CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'www' COMMENT '来自哪个接口',
  `dateline` int(11) NOT NULL DEFAULT 0 COMMENT '写入时间',
  `user_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '会员ID，为0非会员',
  `admin_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '管理员ID，为0非管理员',
  `ctrlid` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '控制器ID',
  `funcid` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '方法ID',
  `url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '执行的URL',
  `status` tinyint(1) NOT NULL DEFAULT 0 COMMENT '0未审核1已审核',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财富获取或消耗日志' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of cst_wealth_log
-- ----------------------------

-- ----------------------------
-- Table structure for cst_wealth_rule
-- ----------------------------
DROP TABLE IF EXISTS `cst_wealth_rule`;
CREATE TABLE `cst_wealth_rule`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '规则ID',
  `wid` int(10) UNSIGNED NOT NULL COMMENT '财产ID',
  `action` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '触发动作',
  `val` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '值，负值表示减，大于0表示加，支持计算如price*2',
  `goal` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'user' COMMENT '目标类型user用户，agent1一级代理',
  `taxis` tinyint(3) UNSIGNED NOT NULL DEFAULT 255 COMMENT '排序',
  `group_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不限制，其他限制这条规则仅在此会员组下的会员有效',
  `uids` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '多个会员ID用英文逗号隔开',
  `qty_type` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'order' COMMENT 'order指订单数，product指产品数',
  `qty` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不限，其他值是在订单数量或产品数量值',
  `price_type` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'order' COMMENT 'order指订单价格，product指产品价格',
  `price` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不限，其他值指订单或产品价格时有效',
  `project_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不限制，其他值限制项目',
  `title_id` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主题限制，多个主题用英文逗号隔开，建议不超过30个主题',
  `goal_group_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不限，其他限制目标会员组ID',
  `goal_uids` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '目标会员ID，多个会员ID用英文逗号隔开',
  `if_stop` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0不中止，1中止',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 24 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '财富生成规则' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of cst_wealth_rule
-- ----------------------------

-- ----------------------------
-- Table structure for cst_world_location
-- ----------------------------
DROP TABLE IF EXISTS `cst_world_location`;
CREATE TABLE `cst_world_location`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键',
  `pid` int(11) UNSIGNED NULL DEFAULT 0 COMMENT '父ID',
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '中文名称',
  `name_en` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '英文名称',
  `code` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '代码',
  `code2` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '2位国际编码',
  `status` tinyint(1) UNSIGNED NULL DEFAULT 0 COMMENT '默认是否显示1，隐藏0',
  `excise_rate` decimal(10, 4) NOT NULL DEFAULT 0.0000 COMMENT '消费税税率，值都是小于1的',
  `tariff_rate` decimal(10, 4) UNSIGNED NOT NULL DEFAULT 0.0000 COMMENT '关税税率，无关税请留0值',
  `lang_code` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '执行的语言包',
  `site_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '锁定站点ID，为0表示默认',
  `tpl_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '模板风格文件，为0读取默认板风格',
  `taxis` int(10) UNSIGNED NOT NULL DEFAULT 255 COMMENT '值越小越往前排',
  `currency_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '货币ID，为0使用站点默认',
  `freight_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费计算方式',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `pid`(`pid`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 4173 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '全球地区库' ROW_FORMAT = Dynamic;

